import { createContext, useContext, useEffect, useState } from "react";
import { LoaderContext } from "./LoaderContext";

const HomePageContext = createContext();

function HomePageContextProvider({ children }) {
  const [homePageData, setHomePageData] = useState([]);
  const [loader, setLoader] = useState(true);


  useEffect(() => {
    fetch("https://www.reddit.com/r/memes.json")
      .then((res) => res.json())
      .then((body) => {
        setHomePageData(body.data.children);
        setLoader(false);
      });
  }, []);

  console.log(homePageData);
  return (
    <HomePageContext.Provider value={[homePageData, setHomePageData, loader, setLoader]}>
      {children}
    </HomePageContext.Provider>
  );
}

export { HomePageContext, HomePageContextProvider };
