import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "../../firebase";
import { useHistory } from "react-router-dom";

export default function SignUp() {
  const [emailIsValid, setEmailIsValid] = useState(false);
  const [error, setError] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);
  const [authData, setAuthData] = useState([]);
  // const [passwordTaken, setPasswordTaken] = useState(false);
  // const [passwordError, setPasswordError] = useState("");
  const [passwordIsValid, setPasswordIsValid] = useState(false);
  const [passwordIsValidError, setPasswordIsValidError] = useState(true);
  const [requireTextField, setRequireTextField] = useState(false);
  const [requiredError, setRequiredError] = useState("");

  const [signError, setSignError] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPasword] = useState("");
  const [passwordMatch, setPasswordMatch] = useState(true);
  const [confirmError, setConfirmError] = useState("");

  function validateEmail(e) {
    const email = e.target.value;
    const regex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    if (regex.test(email)) {
      setEmailIsValid(true);
      setError(false);
    } else {
      setEmailIsValid(false);
      setError(true);
    }
  }

  function tooglePassword() {
    setShowPassword(!showPassword);
  }
  function toogleConfirmPass() {
    setShowConfirmPass(!showConfirmPass);
  }

  // useEffect(() => {
  //   fetch("http://localhost:4000/memebook")
  //     .then((res) => res.json())
  //     .then((data) => {
  //       console.log(data);
  //       setAuthData([...data]);
  //     });
  // }, []);

  // function passwordCheck(authData, e) {
  //   const password = e.target.value;
  //   authData.map((d) => {
  //     if (password === d.password) {
  //       setPasswordTaken(true);
  //       setPasswordError("This Password is already taken!");
  //     } else if (password.length < 8) {
  //       setPasswordTaken(false);
  //       setPasswordError("");
  //     }
  //   });
  // }

  function validatePassword(e) {
    const passWord = e.target.value;
    const passwordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[#$@!%&*?])[A-Za-z\d#$@!%&*?]{8,30}$/;
    if (passwordRegex.test(passWord)) {
      setPasswordIsValid(true);
      setPasswordIsValidError(false);
    } else {
      setPasswordIsValid(false);
      setPasswordIsValidError(true);
    }
  }

  function requiredField(e) {
    if (e.target.value.length === 0) {
      setRequireTextField(true);
      setRequiredError("*required");
    } else {
      setRequireTextField(false);
      setRequiredError("");
    }
  }

  function handleEmailInput(e) {
    setEmail(e.target.value);
  }
  function handlePasswordInput(e) {
    setPassword(e.target.value);
  }

  function handleConfirmPasswordInput(e) {
    setConfirmPasword(e.target.value);
  }

  function confirmPasswordChange() {
    if (password.replace(/\s/g, "") !== confirmPassword.replace(/\s/g, "")) {
      setPasswordMatch(false);
      setConfirmError(true);
    } else {
      setConfirmError(false);
    }
  }

  const history = useHistory();
  function handleSignUpClick() {
    createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        const user = userCredential.user;
        history.push("./login");
      })
      .catch((error) => {
        setSignError(true);
      });
  }

  return (
    <div className="sign-up-form">
      <form>
        <div
          style={{
            textAlign: "center",
            fontSize: "35px",
            fontWeight: "800",
            fontFamily: "Copperplate, Copperplate Gothic Light,fantasy",
          }}
        >
          <label>Sign Up</label>
        </div>
        <div>
          <br />
          <label htmlFor="first-name">First Name: </label>
          <input
            type="text"
            onChange={(e) => {
              // handleInput(e);
              requiredField(e);
            }}
            name="firstname"
            className={requireTextField ? "required-text" : ""}
          />
          <br />
          <small style={{ color: "red" }}>
            <i>{requiredError}</i>
          </small>
        </div>
        <div>
          <br />
          <label htmlFor="last-name">Last Name: </label>
          <input
            type="text"
            name="lastname"
            className={requireTextField ? "required-text" : ""}
            // onChange={handleInput}
          />
          <br />
          <small style={{ color: "red" }}>
            <i>{requiredError}</i>
          </small>
        </div>
        <div>
          <br />
          <label htmlFor="email">Email: </label>
          <input
            type="email"
            name="email"
            onChange={(e) => {
              handleEmailInput(e);
              validateEmail(e);
              requiredField(e);
            }}
            className={requireTextField ? "required-text" : ""}
          />
          <br />
          <small style={{ color: "red" }}>
            <i>{requiredError}</i>
          </small>
          <br />
          <i>
            {error ? (
              <small style={{ color: "red" }}>Your email is not valid</small>
            ) : (
              <small style={{ color: "green" }}>Your email is valid</small>
            )}
          </i>
        </div>
        <div>
          <br />
          <label htmlFor="password">Password: </label>
          <input
            type={showPassword ? "text" : "password"}
            name="password"
            onChange={(e) => {
              handlePasswordInput(e);
              // passwordCheck(authData, e);
              validatePassword(e);
            }}
            className={passwordIsValid ? "" : "required-text"}
          />
          <br />
          <small style={{ color: "red" }}>
            <i>{requiredError}</i>
          </small>
          <br />
          <input type="checkbox" onClick={tooglePassword} />
          <small>{showPassword ? " Hide " : " Show "} Password</small>
          {/* <br />
          <small style={{ color: "red" }}>
            <i>{passwordError}</i>
          </small>
          <br /> */}
          <br />
          <small>
            <i>
              {passwordIsValidError ? (
                <small style={{ color: "red" }}>
                  "Password must be 8-30 characters long and must have at least
                  one Number, Uppercase, Lowercase and a Special character!"
                </small>
              ) : (
                <small style={{ color: "green" }}>Your password is valid</small>
              )}
            </i>
          </small>
        </div>
        <div>
          <br />
          <label htmlFor="confirm-password">Confirm Password: </label>
          <input
            type={showConfirmPass ? "text" : "password"}
            name="confirmPassword"
            onChange={(e) => {
              // handleInput(e);
              requiredField(e);
              handleConfirmPasswordInput(e);
              confirmPasswordChange(e);
            }}
            className={requireTextField ? "required-text" : ""}
          />
          <br />
          <small style={{ color: "red" }}>
            <i>{requiredError}</i>
          </small>
          <br />
          <input type="checkbox" onClick={toogleConfirmPass} />
          <small>{showConfirmPass ? " Hide " : " Show "} Password</small>
          <br />
          <i>{confirmError ? (<small style={{color: "red"}}>Password doesn't match</small>):(<small style={{color: "green"}}>"Password Matches, All Good!!"</small>) }</i>
        </div>
        <div>
          <br />
          <label htmlFor="username">Set UserName: </label>
          <input
            type="text"
            name="username"
            onChange={(e) => {
              // handleInput(e);
              requiredField(e);
            }}
            className={requireTextField ? "required-text" : ""}
          />
          <br />
          <small style={{ color: "red" }}>
            <i>{requiredError}</i>
          </small>
        </div>
        <br /> <br />
        <small>
          <i>
            By clicking the "SignUp" button below, you agree to all terms and
            conditions
          </i>
        </small>
        <br /> <br />
        {signError && (
          <small style={{ color: "red" }}>
            All Fields must be Filled with Valid Data!!
          </small>
        )}
        <div className="landing-signup">
          <button
            to="/login"
            className="signup-button"
            onClick={() => {
              handleSignUpClick();
            }}
          >
            Sign Up
          </button>
        </div>
      </form>
    </div>
  );
}
