import React, { useContext } from "react";
import { HomePageContext } from "../../context/HomePageContext";
import LoadingSpin from "../AuthForm/LoadingSpin";

export default function MemeContainer() {
  const [homePageData, setHomePageData] = useContext(HomePageContext);
  return (
    <div className="meme-container"> 
      {homePageData.slice(2, 26).map((d, index) => {
        if (d.data.post_hint) {
          return <p key={index}>{d.data.title}</p>;
        }
        return <LoadingSpin />;
      })}
    </div>
  );
}
