import "./Upload.scss";
import {useContext} from "react";
import {UploadMemeContext} from "../../context/UploadMemeContext"

export default function Upload({preview, showPreview, removePreview }) {
  const [uploadMeme, setUploadMeme] = useContext(UploadMemeContext);

  const handleCaptionInput = (e) => {
    setUploadMeme({...uploadMeme, [e.target.name]: e.target.value})
  }
  return (
    <div className="container">
      <div className="upload-show">
        <form method="post" className="form">
          <label className="label-top">
            Alright, Lets Upload a Meme<br />
            <br />
            ðŸ¤­
          </label>
          <br /> <br />
          <label forhtml="fullName">Say Something About Your Meme: </label>
          <br/> <br/>
          <input
            type="text"
            style={{height: "100px", width: "350px"}}
            name="caption"
            onChange={handleCaptionInput}
          ></input>
          <br /> <br /> <br /> <br /> <br />
          {preview ? (
          <div className="image">
            <img
              src={URL.createObjectURL(preview)}
              className="img"
              alt="profile-pic"
            />
            <br />
            <button className="remove-image" 
            onClick={removePreview}
            >
              Remove
            </button>
          </div>
        ) : null}
          <br />
          <label htmlFor="file-ip" className="label">
            Choose Your Meme
          </label>
          <input
            name="image"
            type="file"
            id="file-ip"
            accept="image/*"
            onChange={(e) => {
              showPreview(e);
              handleCaptionInput(e);
            }}
            hidden
          ></input>
          <br /> <br />
          <button
            className="save-profile"
            // onClick={handleSave}
            // disabled={imageLoad !== null && imageLoad < 100}
          >
            Upload
          </button>
        </form>
      </div>
    </div>
  );
}
