import React from "react";
import {Link} from "react-router-dom";
import {useState} from "react";
import "/Login.scss";


export default function Login(){
  const [error, setError] = useState(false)

  const handleLogin = (e) => {
    e.preventDefault();
  }
    return(
        <div className="login">
      <form>
        <div
          style={{
            textAlign: "center",
            fontSize: "35px",
            fontWeight: "800",
            fontFamily: "Copperplate, Copperplate Gothic Light,fantasy",
          }}
        >
          <label>Log In</label>
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Email: </label>
          <input type="email" required />
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Password: </label>
          <input type="password" required />
        </div>
        <br/> <br/>      
        <small><i>By clicking the "Log In" button below, you will be directed to the actual app</i></small>
        <br/> <br/>
        <div className="landing-signup">
          <button className="signup-button" onClick={handleLogin}>
            Log In
          </button>
        </div>
        {error && <small style={{color: "red"}}>Wrong Email or Password !!</small>}
      </form>
    </div>
    )
}
