import React, { useContext } from "react";
import { NavLink, useHistory, Link } from "react-router-dom";
import "./NavBar.css";
import Dropdown from "react-bootstrap/Dropdown";
import "bootstrap/dist/css/bootstrap.min.css";
import { HomePageContext } from "../../context/HomePageContext";

export default function NavBar() {
  const [homePageData, setHomePageData, newMeme, setNewMeme] =
    useContext(HomePageContext);
  const getNewMemes = () => {
    fetch(`https://www.reddit.com/r/memes.json?after=${newMeme}`)
      .then((res) => res.json())
      .then((body) => {
        setNewMeme(body.data.after);
        setHomePageData(body.data.children);
      });
  };
  const history = useHistory();
  return (
    <div className="nav-bar">
      <button
        className="meme-book"
        onClick={(e) => {
          history.push("/homeview");
          getNewMemes()
        }}
      >
        Meme <br />
        <span
          style={{
            color: "black",
            fontSize: "50px",
            textShadow: "0 0 2px white",
          }}
        >
          Book
        </span>
      </button>
      {/* <img src="./images/upload.png" alt="upload" className="meme-upload"/> */}
      <Link
        to="/homeview/upload"
        className="meme-upload"
        title="Upload Meme"
      ></Link>
      <Link
        to="/homeview/create"
        className="meme-create"
        title="Create Meme"
      ></Link>
      {/* <button to="/homeview/profile" className="meme-profile"></button> */}
      <Dropdown>
        <Dropdown.Toggle id="custom">
          <NavLink
            to="/homeview/profile"
            className="meme-profile"
            title="Set Profile"
          ></NavLink>
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item
            href=""
            onClick={(e) => {
              history.push("/homeview/setprofile");
            }}
          >
            Set Profile
          </Dropdown.Item>
          <Dropdown.Item
            href=""
            onClick={(e) => {
              window.localStorage.clear();
              history.push("/");
            }}
          >
            Logout
          </Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );
}
