import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";

export default function SignUp() {
  let [signUpData, setSignUpData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    username: "",
  });
  let [emailIsValid, setEmailIsValid] = useState(false);
  let [error, setError] = useState("");
  const { register, handleSubmit, formState: {errors} } = useForm();

  function validateEmail(e) {
    const email = e.target.value;
    const regex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    if (regex.test(email)) {
      setEmailIsValid(true);
      setError("Your email is valid");
    } else {
      setEmailIsValid(false);
      setError("Your email is not valid.");
    }
  }

  function handleInput(e) {
    setSignUpData({ ...signUpData, [e.target.name]: e.target.value });
  }

  function handleSignUpClick() {
    fetch("http://localhost:4000/memebook", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        firstname: signUpData.firstname,
        lastname: signUpData.lastname,
        email: signUpData.email,
        password: signUpData.password,
        username: signUpData.username,
      }),
    });
  }

  return (
    <div className="sign-up-form">
      <form>
        <div
          style={{
            textAlign: "center",
            fontSize: "35px",
            fontWeight: "800",
            fontFamily: "Copperplate, Copperplate Gothic Light,fantasy",
          }}
        >
          <label>Sign Up</label>
        </div>
        <div>
          <br />
          <label htmlFor="first-name">First Name: </label>
          <input
            type="text"
            onChange={handleInput}
            name="firstname"
            {...register("firstname", {
              required: "Required",
            })}
          />
        </div>
        <div>
          <br />
          <label htmlFor="last-name">Last Name: </label>
          <input
            type="text"
            name="lastname"
            onChange={handleInput}
            {...register("firstname", {
              required: "Required",
            })}
          />
        </div>
        <div>
          <br />
          <label htmlFor="email">Email: </label>
          <input
            type="email"
            name="email"
            onChange={(e) => {
              handleInput(e);
              validateEmail(e);
            }}
            {...register("firstname", {
              required: "Required",
            })}
          />
          <br />
          <small>
            <i>{error}</i>
          </small>
        </div>
        <div>
          <br />
          <label htmlFor="psssword">Password: </label>
          <input
            type="password"
            name="password"
            onChange={handleInput}
            {...register("firstname", {
              required: "Required",
            })}
          />
          <input type="checkbox" />
          <small>Show Password</small>
        </div>
        <div>
          <br />
          <label htmlFor="confirm-password">Confirm Password: </label>
          <input
            type="password"
            name="password"
            onChange={handleInput}
            {...register("firstname", {
              required: "Required",
            })}
          />
        </div>
        <div>
          <br />
          <label htmlFor="username">Set UserName: </label>
          <input
            type="text"
            name="username"
            onChange={handleInput}
            {...register("firstname", {
              required: "Required",
            })}
          />
        </div>
        <br /> <br />
        <small>
          <i>
            By clicking the "SignUp" button below, you agree to all terms and
            conditions
          </i>
        </small>
        <br /> <br />
        <div className="landing-signup">
          <Link
            to="/login"
            className="signup-button"
            onClick={handleSubmit(handleSignUpClick)}
          >
            Sign Up
          </Link>
        </div>
      </form>
    </div>
  );
}
