import { ProfileContext } from "../../context/ProfileContext";
import { useContext, useState } from "react";
import {useHistory} from "react-router-dom";

export default function CreateMeme({
  preview,
  setPreview,
  showPreview,
  removePreview,
}) {
  const [profile, setProfile] = useContext(ProfileContext);
  const [imageLoad, setImageLoad] = useState(null);
  const history = useHistory();

  return (
    <div className="container">
      <div className="upload-show">
        <form method="post" className="form">
          <label className="label-top">
            Alright, Lets Create a Meme
            <br />
            <br />
            🤭
          </label>
          <br /> <br />
          <br /> <br /> <br />
          <br />
          {preview ? (
            <div className="image">
              <img
                src={URL.createObjectURL(preview)}
                className="img"
                alt="profile-pic"
              />
              <br />
              <button className="remove-image" onClick={removePreview}>
                Remove
              </button>
            </div>
          ) : null}
          <br /> <br />
          <small>Upload Progress: {imageLoad}% done!!</small>
          <br />
          <label htmlFor="file-ip" className="label">
            Choose Your Meme
          </label>
          <input
            type="file"
            id="file-ip"
            accept="image/*"
            onChange={(e) => {
              showPreview(e);
              // handleCaptionInput(e);
            }}
            hidden
          ></input>
          <br /> <br /> <br />
          <label forhtml="fullName">Say Something About Your Meme: </label>
          <br /> <br />
          <input
            type="text"
            style={{ height: "auto", width: "auto" }}
            name="caption"
            // onChange={handleCaptionInput}
          ></input>
          <br /> <br />
          <button
            className="save-profile"
            // onClick={handleUpload}
            disabled={imageLoad !== null && imageLoad < 100}
          >
            Save
          </button>
        </form>
      </div>
    </div>
  );
}
