import "../App.css";
import { Route } from "react-router-dom";
import { BrowserRouter as Router ,Switch, useState, Redirect } from "react-router-dom";
import LandingPage from "./AuthForm/LandingPage";
import SignUp from "./AuthForm/SignUp";
import Login from "./AuthForm/Login";
import Footer from "./AuthForm/Footer";
import HomePage from "./AppContents/HomePage";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";



function App() {
  const [currentUser, setCurrentUser] = useContext(AuthContext);



  return (
    <div>
      <Router>
        <Switch>
          <Route path="/signup">
            <SignUp />
          </Route>
          <Route path="/login">
            <Login />
            </ Route>
          <Route path="/homeview">
              {currentUser ? <HomePage/> :<Redirect to="/login" />}
          </Route>
          <Route path="/">
            <LandingPage />
          </Route>
        </Switch>
        </Router>
      <Footer />
    </div>
  );
}

export default App;
