import React, { useContext } from "react";
import { HomePageContext } from "../../context/HomePageContext";
import "./MemeContainer.css";

export default function MemeContainer() {
  const [homePageData, setHomePageData] = useContext(HomePageContext);
  const memeToDisplay = homePageData.slice(2, 12).map((d, index) => {
    if (d.data.post_hint) {
      const image_url = d.data.url_overriden_by_dest;
      return (
        <div className="meme-container" key={index}>
          <p>{d.data.title}</p>
          <img src={image_url} alt={d.data.title} className="meme-image"></img>
        </div>
      );
    }
  });

  return <div className="container">{memeToDisplay}</div>;
}
