import { ProfileContext } from "../../context/ProfileContext";
import { useContext, useEffect, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { CreateMemeContext } from "../../context/CreateMemeContext";
import "./CreateMeme.css";

export default function CreateMeme() {
  const [createMeme, setCreateMeme] = useContext(CreateMemeContext);
  const [captionText, setCaptionText] = useState([]);
  const [imageForMeme, setImageForMeme] = useState();
  const [memesFromApi, setMemesFromApi] = useState();
  const [imageLoad, setImageLoad] = useState(null);
  const history = useHistory();

  // function SeePreview(e) {
  //   if (e.target.files && e.target.files.length > 0) {
  //     const src = e.target.files[0];
  //     setImageForMeme(src);
  //   }
  // }

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes")
      .then((res) => res.json())
      .then((body) => {
        setMemesFromApi(body.data.memes);
      });
  }, []);

  useEffect(()=> {
    console.log(captionText)
  }, [captionText])

  console.log(memesFromApi);

  const handleGenerate = (e) => {
    e.preventDefault();
    const randomMemeTemplate = Math.floor(Math.random() * memesFromApi.length);
    const randomMemeTemplateUrl = memesFromApi[randomMemeTemplate].url
    setImageForMeme(randomMemeTemplateUrl);
    setCaptionText(Array(memesFromApi[randomMemeTemplate].box_count).fill(" "))
    console.log(captionText);
    console.log(imageForMeme);
    history.push(`/homeview/create/${memesFromApi[randomMemeTemplate].name}`)

  };


  const handleInputChange = (e, index) => {
    const text = e.target.value;

    setCaptionText(
      captionText.map((c, i ) => {
        if(index === i) {
          return text;
        }else{
          return c;
        }
      })
    );
  };

  // useEffect(() => {
  //   if(memesFromApi) {
  //     setCaptionText(memesFromApi.map().box_count.fill(''))
  //   }
  // })

  // useEffect(() => {
  //   console.log(memesFromApi.box_count)
  // },[inputText])

  const handleReset = (e) => {
    e.preventDefault();
    setCaptionText({
      topText: "",
      bottomText: "",
    });
  };

  // console.log(imageForMeme);
  return (
    <div className="container">
      <div className="create-show">
        <form method="post" className="form">
          <label className="label-top">
            Alright, Lets Create a Meme
            <br />
            <br />
            ðŸ¤­
          </label>
          <br /> <br />
          <button className="label" onClick={handleGenerate}>
            Generate Meme Template
          </button>
          <br /> 
          {captionText.map((c, index) => {
           return <input key={index}  style={{margin: "5px"}} placeholder="Enter your Texts" onChange={(e)=> {
             handleInputChange(e, index)
           }}/>
          })}
          {/* <label forhtml="fullName">Top Text: </label>
          <br />
          <textarea
            type="text"
            // value={inputText.topText}
            name="topText"
            // onChange={handleInputChange}
          ></textarea>
          <br /> 
          <label forhtml="fullName">Bottom Text: </label>
          <br />
          <textarea
            type="text"
            // value={inputText.bottomText}
            name="bottomText"
            // onChange={handleInputChange}
          ></textarea> */}
          {/* <small>Upload Progress: {imageLoad}% done!!</small> */}
          <br />
          {/* <input
            name="creatingMeme"
            type="file"
            id="file-ip"
            accept="image/*"
            onChange={SeePreview}
            hidden
          ></input> */}

          {/* {imageForMeme ? (
            <div className="meme">
                <img src={URL.createObjectURL(imageForMeme)} alt="meme" />
                <h2 className="top">{inputText.topText}</h2>
                <h2 className="bottom">{inputText.bottomText}</h2>
            </div>
          ) : null} */}
          {imageForMeme ? (
          <div className="meme">
            <img src={imageForMeme} alt="meme" />
            {/* <h2 className="top"></h2>
            <h2 className="bottom"></h2> */}
          </div>) :null }
          <br />
          <button className="reset-image" onClick={handleReset}>
            Reset
          </button>
          <br /> <br />
          <button
            className="save-profile"
            // onClick={handleUpload}
            disabled={imageLoad !== null && imageLoad < 100}
          >
            Generate
          </button>
        </form>
      </div>
    </div>
  );
}
