import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function SignUp() {
  let [signUpData, setSignUpData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    username: "",
  });
  let [emailIsValid, setEmailIsValid] = useState(false);
  let [error, setError] = useState("");

  function validateEmail(e) {
    const email = e.target.value;
    // const regex = /\S+@\S+\.\S+/;
    const regex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    if (regex.test(email)) {
      setEmailIsValid(true);
      setError("Your email is valid");
    } else {
      setEmailIsValid(false);
      setError('Your email is not valid.')
    }
  }

  function handleInput(e) {
    setSignUpData({ ...signUpData, [e.target.name]: e.target.value });
  }

  function handleSignUpClick() {
    fetch("http://localhost:4000/memebook", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        firstname: signUpData.firstname,
        lastname: signUpData.lastname,
        email: signUpData.email,
        password: signUpData.password,
        username: signUpData.username,
      }),
    });
  }

  return (
    <div className="sign-up-form">
      <form>
        <div
          style={{
            textAlign: "center",
            fontSize: "35px",
            fontWeight: "800",
            fontFamily: "Copperplate, Copperplate Gothic Light,fantasy",
          }}
        >
          <label>Sign Up</label>
        </div>
        <div>
          <br />
          <label htmlFor="first-name">First Name: </label>
          <input type="text" onChange={handleInput} name="firstname" />
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Last Name: </label>
          <input type="text" name="lastname" onChange={handleInput} />
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Email: </label>
          <input type="email" name="email" onChange={e => {handleInput(e); validateEmail(e)}} />
          <small><i>{error}</i></small>
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Password: </label>
          <input type="password" name="password" onChange={handleInput} />
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Confirm Password: </label>
          <input type="password" name="password" onChange={handleInput} />
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Set UserName: </label>
          <input type="text" name="username" onChange={handleInput} />
        </div>
        <br /> <br />
        <small>
          <i>
            By clicking the "SignUp" button below, you agree to all terms and
            conditions
          </i>
        </small>
        <br /> <br />
        <div className="landing-signup">
          <Link
            to="/login"
            className="signup-button"
            onClick={handleSignUpClick}
          >
            Sign Up
          </Link>
        </div>
      </form>
    </div>
  );
}
