export const AuthReducer = (state, action) => {
  if (action.type === "LOGIN") {
    return {
      currentUser: action.payload,
    };
  } else if (action.type === "LOGOUT") {
    return {
      currentUser: null,
    };
  } else{
      return state;
  }
};
