import React from "react";
import { useHistory } from "react-router-dom";
import { useState } from "react";
import "./Login.scss";
import { signInWithEmailAndPassword } from "firebase/auth";
import { auth } from "../../firebase";

export default function Login() {
  const [error, setError] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emptyError, setEmptyError] = useState (false);

  const history = useHistory()

  const emailChange = (e) => {
    setEmail(e.target.value)
  };

  const passwordChange = (e) => {
    setPassword(e.target.value)
  }

  const handleLogin = (e) => {
    e.preventDefault();

    signInWithEmailAndPassword(auth, email, password,)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        console.log(user);
        history.push("/homeview")
      })
      .catch((error) => {
        setError(true)
      });
  };
  return (
    <div className="login">
      <form>
        <div
          style={{
            textAlign: "center",
            fontSize: "35px",
            fontWeight: "800",
            fontFamily: "Copperplate, Copperplate Gothic Light,fantasy",
          }}
        >
          <label>Log In</label>
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Email: </label>
          <input type="email" onChange={emailChange} />
        </div>
        <div>
          <br />
          <label htmlFor="first-name">Password: </label>
          <input type="password" onChange={passwordChange} />
        </div>
        <br /> <br />
        <small>
          <i>
            By clicking the "Log In" button below, you will be directed to the
            actual app
          </i>
        </small>
        <br /> <br />
        <div className="landing-signup">
          <button className="signup-button" onClick={handleLogin}>
            Log In
          </button>
        </div>
        {error && (
          <small style={{ color: "red" }}>Wrong Email or Password !!</small>
        )}
      </form>
    </div>
  );
}
