import React, { useContext, useEffect } from "react";
import { ProfileContext } from "../../context/ProfileContext";
import "./SetUpAccount.scss";
import { doc, setDoc, serverTimestamp } from "firebase/firestore";
import { db, auth, storage } from "../../firebase";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";

export default function SetUpAccount({ preview, showPreview, removePreview }) {
  const [profile, setProfile] = useContext(ProfileContext);





  const handleInputChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const user = auth.currentUser;
      await setDoc(doc(db, "users", user.uid), {
        ...profile,
        timeStamp: serverTimestamp(),
      });
    } catch (error) {
      console.log(error);
    }
}

useEffect(() => {
    const name = new Date().getTime();
    console.log(name);
    const storageRef = ref(storage, name);
    const uploadTask = uploadBytesResumable(storageRef, preview);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const progress =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log("Upload is " + progress + "% done");
        switch (snapshot.state) {
          case "paused":
            console.log("Upload is paused");
            break;
          case "running":
            console.log("Upload is running");
            break;
          default:
            break;
        }
      },
      (error) => {
        console.log(error);
      },
      () => {
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          console.log("File available at", downloadURL);
          setProfile({ ...profile, img: downloadURL });
        });
      }
    );
  })


  return (
    <div className="setup-show">
      <form method="post" className="form" >
        <label className="label-top">
          Lets Create A Profile First..hehe 🤭
        </label>
        <br /> <br /> <br />
        <label forhtml="fullName">Full Name: </label>
        <input type="text" name="fullName" onChange={handleInputChange}></input>
        <br /> <br />
        <label forhtml="fullName">username: </label>
        <input type="text" name="username" onChange={handleInputChange}></input>
        <br /> <br />
        {preview ? (
          <div className="image">
            <img
              src={URL.createObjectURL(preview)}
              className="img"
              alt="profile-pic"
            />
            <br />
            <button className="remove-image" onClick={removePreview}>
              Remove
            </button>
          </div>
        ) : null}
        <br />
        <label htmlFor="file-ip" className="label">
          Choose Profile Picture{" "}
        </label>
        <input
          name="image"
          type="file"
          id="file-ip"
          accept="image/*"
          onChange={(e) => {
            showPreview(e);
            handleInputChange(e);
          }}
          hidden
        ></input>
        <br /> <br />
        <button className="save-profile" onClick={(e) => {
          handleSave(e)
      }}>

          Save
        </button>
      </form>
    </div>
  );
}
