import "./Upload.scss";
import {useContext, useState, useEffect} from "react";
import {UploadMemeContext} from "../../context/UploadMemeContext";
import { ProfileContext } from "../../context/ProfileContext";
import {useHistory} from "react-router-dom";
import { doc, setDoc, serverTimestamp } from "firebase/firestore";
import { db, auth, storage } from "../../firebase";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";

export default function Upload({preview, setPreview, showPreview, removePreview }) {
  const [uploadMeme, setUploadMeme] = useContext(UploadMemeContext);
  const [profile, setProfile] = useContext(ProfileContext);
  const [setupShow, setSetupShow] = useState(true);
  const [imageLoad, setImageLoad] = useState(null);
  const history = useHistory();

  const handleCaptionInput = (e) => {
    setUploadMeme({...uploadMeme, [e.target.name]: e.target.value})
  }

  const handleUpload = async (e) => {
    e.preventDefault();
    try {
      const user = auth.currentUser;
      await setDoc(doc(db, "users", user.uid), {
        ...profile,
        ...uploadMeme,
      });
    } catch (error) {
      console.log(error);
    }
    history.push("/homeview");
    setSetupShow(false);

  };

  useEffect(() => {
    const uploadFile = () => {
      const name = new Date().getTime() + preview.name;
      console.log(name);
      const storageRef = ref(storage, preview.name);
      const uploadTask = uploadBytesResumable(storageRef, preview);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Upload is " + progress + "% done");
          setImageLoad(progress);
          switch (snapshot.state) {
            case "paused":
              console.log("Upload is paused");
              break;
            case "running":
              console.log("Upload is running");
              break;
            default:
              break;
          }
        },
        (error) => {
          console.log(error);
        },
        () => {
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            console.log("File available at", downloadURL);
            setProfile({ ...profile, meme: downloadURL });
          });
        }
      );
    };
    preview && uploadFile();
  }, [preview]);
  return (
    <div className="container">
      <div className="upload-show">
        <form method="post" className="form">
          <label className="label-top">
            Alright, Lets Upload a Meme<br />
            <br />
            ðŸ¤­
          </label>
          <br /> <br />
          <label forhtml="fullName">Say Something About Your Meme: </label>
          <br/> <br/>
          <input
            type="text"
            style={{height: "80px", width: "auto"}}
            name="caption"
            onChange={handleCaptionInput}
          ></input>
          <br /> <br /> <br /> <br /> <br />
          {preview ? (
          <div className="image">
            <img
              src={URL.createObjectURL(preview)}
              className="img"
              alt="profile-pic"
            />
            <br />
            <button className="remove-image" 
            onClick={removePreview}
            >
              Remove
            </button>
          </div>
        ) : null}
          <br />
          <label htmlFor="file-ip" className="label">
            Choose Your Meme
          </label>
          <input
            name="meme"
            type="file"
            id="file-ip"
            accept="image/*"
            onChange={(e) => {
              showPreview(e);
              handleCaptionInput(e);
            }}
            hidden
          ></input>
          <br /> <br />
          <button
            className="save-profile"
            onClick={handleUpload}
            disabled={imageLoad !== null && imageLoad < 100}
          >
            Upload
          </button>
        </form>
      </div>
    </div>
  );
}
