import React, { useContext } from "react";
import { HomePageContext } from "../../context/HomePageContext";
import "./MemeContainer.css";
import { ProfileContext } from "../../context/ProfileContext";

export default function MemeContainer() {
  const [homePageData, setHomePageData] = useContext(HomePageContext);
  const [profile, setprofile] = useContext(ProfileContext);
  const memeToDisplay = homePageData.slice(2, 12).map((d, index) => {
    if (d.data.post_hint === "image") {
      const image_url = d.data.url_overridden_by_dest;
      return (
        <div className="meme-container" key={index}>
          <h4 style={{ float: "left" }}>{d.data.author}:</h4>
          <br />
          <small
            style={{
              fontStyle: "italic",
              fontWeight: "300",
              fontSize: "15px",
              float: "left",
            }}
          >
            {d.data.title}
          </small>
          <br/> <br/>
          <img src={image_url} alt={d.data.title} className="meme-image" />
          <br/> <br/>
          {/* <button>View Comments</button>
          <small style={{fontSize: "10px"}}></small> */}
        </div>
      );
    }
  });

  const myMeme = localStorage.getItem(JSON.stringify(profile.fullName))
  console.log(myMeme)

  return <div>
    <div className="meme-container">
          <h4 style={{ float: "left" }}>Ajay:</h4>
          <br />
          <small
            style={{
              fontStyle: "italic",
              fontWeight: "300",
              fontSize: "15px",
              float: "left",
            }}
          >
            wassuuup
          </small>
          <br/> <br/>
          <img src="./user.png" alt="do i care" className="meme-image" />
          <br/> <br/>
          {/* <button>View Comments</button>
          <small style={{fontSize: "10px"}}></small> */}
        </div>
    {memeToDisplay}</div>;
}
