import { ProfileContext } from "../../context/ProfileContext";
import { useContext, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { CreateMemeContext } from "../../context/CreateMemeContext";
import "./CreateMeme.css";

export default function CreateMeme() {
  const [createMeme, setCreateMeme] = useContext(CreateMemeContext);
  const [inputText, setInputText] = useState({});
  const [imageForMeme, setImageForMeme] = useState();
  const [imageLoad, setImageLoad] = useState(null);
  const history = useHistory();

  function SeePreview(e) {
    if (e.target.files && e.target.files.length > 0) {
      const src = e.target.files[0];
      setImageForMeme(src);
    }
  }

  const handleInputChange = (e) => {
    setInputText({ ...inputText, [e.target.name]: e.target.value });
  };

  const handleReset = (e) => {
    e.preventDefault();
    setInputText({
      topText: "",
      bottomText: "",
    });
  };

  const ConvertSvgToImage = () => {
    let svgRef = useRef()
    let svgData = new XMLSerializer().serializeToString(svgRef.current);
    const canvas = document.createElement("canvas");
    canvas.setAttribute("id", "canvas");
    const svgSize = svgRef.getBoundingClientRect();
    canvas.width = svgSize.width;
    canvas.height = svgSize.height;
    const img = document.createElement("img");
    img.setAttribute(
      "src",
      "data:image/svg+xml;base64," + btoa(unescape(encodeURIComponent(svgData)))
    );
    img.onload = function () {
      canvas.getContext("2d").drawImage(img, 0, 0);
      const canvasdata = canvas.toDataURL("image/png");
      const a = document.createElement("a");
      a.download = "meme.png";
      a.href = canvasdata;
      document.body.appendChild(a);
      a.click();
    };
  };

  console.log(imageForMeme);
  return (
    <div className="container">
      <div className="upload-show">
        <form method="post" className="form">
          <label className="label-top">
            Alright, Lets Create a Meme
            <br />
            <br />
            ðŸ¤­
          </label>
          <br /> <br />
          <br /> <br /> <br />
          <label forhtml="fullName">Top Text: </label>
          <br /> <br />
          <textarea
            type="text"
            rows="5"
            cols="20"
            value={inputText.topText}
            name="topText"
            onChange={handleInputChange}
          ></textarea>
          <br /> <br /> <br />
          <label forhtml="fullName">Bottom Text: </label>
          <br /> <br />
          <textarea
            type="text"
            rows="5"
            cols="20"
            value={inputText.bottomText}
            name="bottomText"
            onChange={handleInputChange}
          ></textarea>
          <br /> <br />
          <br /> <br />
          {/* <small>Upload Progress: {imageLoad}% done!!</small> */}
          <br />
          <label htmlFor="file-ip" className="label">
            Choose Your Meme
          </label>
          <input
            name="creatingMeme"
            type="file"
            id="file-ip"
            accept="image/*"
            onChange={SeePreview}
            hidden
          ></input>
          <br /> <br />
          {imageForMeme ? (
            <div className="meme">
              <svg
                id="svg_ref"
                xmlns="http://www.w3.org/2000/svg"
                xmlnsXlink="http://www.w3.org/1999/xlink"
              >
                <img src={URL.createObjectURL(imageForMeme)} alt="meme" />
                <h2 className="top">{inputText.topText}</h2>
                <h2 className="bottom">{inputText.bottomText}</h2>
              </svg>
            </div>
          ) : null}
          <br />
          <button className="reset-image" onClick={handleReset}>
            Reset
          </button>
          <br /> <br />
          <button
            className="save-profile"
            // onClick={handleUpload}
            disabled={imageLoad !== null && imageLoad < 100}
          >
            Generate
          </button>
        </form>
      </div>
    </div>
  );
}
