import { ProfileContext } from "../../context/ProfileContext";
import { useContext, useEffect, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { CreateMemeContext } from "../../context/CreateMemeContext";
import "./CreateMeme.css";
import { UploadMemeContext } from "../../context/UploadMemeContext";
import { db, auth, storage } from "../../firebase";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { collection,addDoc} from "firebase/firestore";

export default function CreateMeme() {
  const [createMeme, setCreateMeme] = useContext(CreateMemeContext);
  const [inputText, setInputText] = useState([]);
  // const [topMemeText, setTopMemetext] = useState("");
  // const [bottomMemeText, setBottomMemetext] = useState("");
  const [imageForMeme, setImageForMeme] = useState();
  const [memesFromApi, setMemesFromApi] = useState();
  const [currentGeneratedMeme, setCurrentGeneratedMeme] = useState();
  const history = useHistory();
  const [caption, setCaption] = useState("");
  const [imageLoad, setImageLoad] = useState(null);

  // function SeePreview(e) {
  //   if (e.target.files && e.target.files.length > 0) {
  //     const src = e.target.files[0];
  //     setImageForMeme(src);
  //   }
  // }

  useEffect(() => {
    fetch("https://api.imgflip.com/get_memes")
      .then((res) => res.json())
      .then((body) => {
        setMemesFromApi([...body.data.memes]);
      });
  }, []);

  const handleInputChange = (e) => {
    setInputText({ ...inputText, [e.target.name]: e.target.value });
    console.log(inputText.topText);
  };

  useEffect(() => {
    const uploadFile = () => {
      const name = new Date().getTime() + createMeme.name;
      console.log(name);
      const storageRef = ref(storage, createMeme.name);
      const uploadTask = uploadBytesResumable(storageRef, createMeme);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Upload is " + progress + "% done");
          setImageLoad(progress);
          switch (snapshot.state) {
            case "paused":
              console.log("Upload is paused");
              break;
            case "running":
              console.log("Upload is running");
              break;
            default:
              break;
          }
        },
        (error) => {
          console.log(error);
        },
        () => {
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            console.log("File available at", downloadURL);
            setCreateMeme({ ...createMeme, uploadedCreaTEMeme: downloadURL });
          });
        }
      );
    };
    currentGeneratedMeme && uploadFile();
  }, [currentGeneratedMeme]);

  const memesINeed = memesFromApi?.filter((meme) => {
    return meme.box_count === 2;
  });

  const handleGenerate = (e) => {
    e.preventDefault();
    const randomMemeTemplate = Math.floor(Math.random() * memesINeed.length);
    setCurrentGeneratedMeme(memesINeed[randomMemeTemplate]);
    const randomMemeTemplateUrl = memesINeed[randomMemeTemplate].url;
    setImageForMeme(randomMemeTemplateUrl);
    console.log(memesINeed[randomMemeTemplate].box_count);
    history.push(`/homeview/create/${memesINeed[randomMemeTemplate].name}`);
  };

  const handleCreate = (e) => {
    e.preventDefault();
    const currentMeme = currentGeneratedMeme;
    console.log(currentMeme);
    const formData = new FormData();

    formData.append("username", "ajay.gh");
    formData.append("password", "Dharan123");
    formData.append("template_id", currentMeme.id);
    formData.append("text0", inputText.topText);
    formData.append("text1", inputText.bottomText);

    fetch("https://api.imgflip.com/caption_image", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((body) => {
        console.log(body);
        setCreateMeme(body.data);
      })
      .catch((error) => console.log("Error:", error));
  };

  const handleReset = (e) => {
    e.preventDefault();
    setInputText({
      topText: "",
      bottomText: "",
    });
  };

  // console.log(imageForMeme);
  return (
    <div className="container">
      <div className="create-show">
        <form method="post" className="form">
          <label className="label-top">
            Alright, Lets Create a Meme
            <br />
            <br />
            ðŸ¤­
          </label>
          <br /> <br />
          <button className="label" onClick={handleGenerate}>
            Generate Meme Template
          </button>
          <br />
          <label forhtml="fullName">Top Text: </label>
          <br />
          <textarea
            type="text"
            name="topText"
            onChange={handleInputChange}
          ></textarea>
          <br />
          <label forhtml="fullName">Bottom Text: </label>
          <br />
          <textarea
            type="text"
            name="bottomText"
            onChange={handleInputChange}
          ></textarea>
          {/* <small>Upload Progress: {imageLoad}% done!!</small> */}
          <br />
          {/* <input
            name="creatingMeme"
            type="file"
            id="file-ip"
            accept="image/*"
            onChange={SeePreview}
            hidden
          ></input> */}
          {/* {imageForMeme ? (
            <div className="meme">
                <img src={URL.createObjectURL(imageForMeme)} alt="meme" />
                <h2 className="top">{inputText.topText}</h2>
                <h2 className="bottom">{inputText.bottomText}</h2>
            </div>
          ) : null} */}
          {imageForMeme ? (
            <div className="meme">
              <img src={imageForMeme} alt="meme" />
              <h2 className="top">{inputText.topText}</h2>
              <h2 className="bottom">{inputText.bottomText}</h2>
            </div>
          ) : null}
          <br />
          <button className="reset-image" onClick={handleReset}>
            Reset
          </button>
          <br /> <br />
          <button className="save-profile" onClick={handleCreate}>
            Generate
          </button>
          <br/> <br/>
          <input
            type="text"
            placeholder="Caption..."
          ></input>
          <button className="save-profile" onClick={handleCreate}>
            Share
          </button>
        </form>
      </div>
    </div>
  );
}
