import { createContext, useEffect, useState } from "react";

const HomePageContext = createContext();

function HomePageContextProvider({ children }) {
  const [homePageData, setHomePageData] = useState([]);

  useEffect(() => {
    fetch("https://www.reddit.com/r/memes.json")
      .then((res) => res.json())
      .then((body) => {
        for(let i=0; i<body.data.children.length; i++){
            if(body.data.children[i].data.post_hint === "image"){
                setHomePageData(body.data.children[i]);
            }
        }
      });
  }, []);

  return (
    <HomePageContext.Provider value={[homePageData, setHomePageData]}>
      {children}
    </HomePageContext.Provider>
  );
}

export { HomePageContext, HomePageContextProvider };
