import React, { useState } from "react";
import "./SetUpAccount.scss";
import {
  addDoc,
  collection,
  doc,
  setDoc,
  serverTimestamp,
} from "firebase/firestore";
import { db } from "../../firebase";

export default function SetUpAccount({ preview, showPreview, removePreview }) {
  const [profile, setProfile] = useState({});

  const handleInputChange = (e) => {
      setProfile({...profile, [e.target.name]: e.target.value})
  }

  console.log(profile)

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const response = await addDoc(collection(db, "cities"), {
        name: "Los Angeles",
        state: "CA",
        country: "USA",
        timeStamp: serverTimestamp(),
      });
    } catch (error) {
      console.log(error);
    }

    // console.log(response.id);
  };
  return (
    <div className="setup">
      <form method="post" className="form" onSubmit={handleSave}>
        <label className="label-top">
          Lets Create A Profile First..hehe 🤭
        </label>
        <br /> <br /> <br />
        <label forhtml="fullName">Full Name: </label>
        <input type="text" name="fullName" onChange={handleInputChange}></input>
        <br /> <br />
        <label forhtml="fullName">username: </label>
        <input type="text" name="username" onChange={handleInputChange}></input>
        <br /> <br />
        {preview ? (
          <div className="image">
            <img
              src={URL.createObjectURL(preview)}
              className="img"
              alt="profile-pic"
            />
            <br />
            <button className="remove-image" onClick={removePreview}>
              Remove
            </button>
          </div>
        ) : null}
        <br />
        <label htmlFor="file-ip" className="label">
          Choose Profile Picture{" "}
        </label>
        <input
          name="image"
          type="file"
          id="file-ip"
          accept="image/*"
          onChange={(e) => {showPreview(e); handleInputChange(e)} }
          hidden

        ></input>
        <br /> <br />
        <button className="save-profile" type="submit">
          {" "}
          Save
        </button>
      </form>
    </div>
  );
}
